/* eslint-disable react/state-in-constructor */
// Basic Imports
import React, { useEffect } from 'react';
import axios from 'axios';
import { connect } from 'react-redux';
import { TabPanel } from 'react-tabs';

/* Styles */
import styles from './index.module.scss';

/* Components */
import PageHeader from '../../components/PageHeader';
import CustomersTable from '../../containers/Customers/CustomersTable';
import CTAButton from '../../components/CTAButton';
import TabsWrapper from '../../components/TabsWrapper';

/* Data */
import { campaigns } from '../../consts/brandCampaigns';

const CustomersPage = () => {
	const [selectedTabList, setSelectedTabList] = React.useState<number>(0);
	const [userData, setUserData] = React.useState<any>();
	useEffect(() => {
		axios
			.get('/customers')
			.then((res) => {
				setUserData(res);
				console.log(userData);
			})
			.catch((err) => {
				console.log(err);
			});
		console.log(userData);
	}, []);
	const handleTabChange = (index: number) => {
		setSelectedTabList(index);
	};
	return (
		<>
			<main className='page-container'>
				<PageHeader
					title='Customers'
					breadcrumb={[{ content: 'Home', to: '/' }, { content: 'Customers' }]}
					callToAction=''
				/>
				<div className={styles.campaignPageHeaderRow}>
					<h2 className={styles.pageTitle}> Customers </h2>
					<div className={styles.buttonsRow}>
						<CTAButton
							colorScheme='brand'
							type='secondary'
							size='tiny'
							to='/brand/campaigns/new'
							disabled
						>
							<i className='bx bxs-download'></i>
							Export
						</CTAButton>
{/* 
						<CTAButton
							colorScheme="brand"
							type="primary"
							size="tiny"
							to="/brand/campaigns/new"
						>
							<i className="bx bx-plus"></i>
							Create New
						</CTAButton> */}
					</div>
				</div>
				<TabsWrapper
					handleTabChange={handleTabChange}
					selectedTab={selectedTabList}
					tabsDisplayList={['All']}
				>
					{userData?.data && (<TabPanel>
						<CustomersTable campaigns={userData.data} />
					</TabPanel>)}
				</TabsWrapper>
			</main>
		</>
	);
};

export default CustomersPage;
