/* eslint-disable react/state-in-constructor */
// Basic Imports
import React, { useEffect } from 'react';
// import { connect } from 'react-redux';
import { TabPanel } from 'react-tabs';


/* Styles */
import axios from 'axios';
import styles from './index.module.scss';

/* Components */
import PageHeader from '../../components/PageHeader';
import OrdersTable from '../../containers/OrdersPage/OrdersTable';
import CTAButton from '../../components/CTAButton';
import TabsWrapper from '../../components/TabsWrapper';

const OrdersPage = () => {
	const [selectedTabList, setSelectedTabList] = React.useState<number>(0);
	const [userData, setUserData] = React.useState<any>();
	useEffect(() => {
		axios.get('/merchants/3/orders').then(
			(res) => {setUserData(res); console.log(userData)}).catch((err)=> {console.log(err)}
		);
		console.log(userData)
	  }, []);
	const handleTabChange = (index: number) => {
		setSelectedTabList(index);
	};
	return (
		<>
			<main className="page-container">
				<PageHeader
					title="Orders"
					breadcrumb={[
						{ content: 'Home', to: '/orders' },
						{ content: 'Orders' }
					]}
					callToAction=""
				/>
				<div className={styles.campaignPageHeaderRow}>
					<h2 className={styles.pageTitle}> Subscriptions </h2>
					<div className={styles.buttonsRow}>
						<CTAButton
							colorScheme="brand"
							type="secondary"
							size="tiny"
							to="/brand/campaigns/new"
							disabled
						>
							<i className="bx bxs-download"></i>
							Export
						</CTAButton>

						{/* <CTAButton
							colorScheme="brand"
							type="primary"
							size="tiny"
							to="/brand/campaigns/new"
						>
							<i className="bx bx-plus"></i>
							Create New
						</CTAButton> */}
					</div>
				</div>
				<TabsWrapper
					handleTabChange={handleTabChange}
					selectedTab={selectedTabList}
					tabsDisplayList={['Active Subscriptions', 'Expired Subscriptions']}
				>
					{userData?.data && (<TabPanel>
						<OrdersTable campaigns={userData.data.filter(
							(campaign: any) => campaign.status === 'Active'
						)} />
					</TabPanel>)}

					{userData?.data && (<TabPanel>
						<OrdersTable campaigns={userData.data.filter(
							(campaign: any) => campaign.status === 'Closed'
						)} />
					</TabPanel>)}
				</TabsWrapper>
			</main>
		</>
	);
};

export default OrdersPage;
